import React, { useState } from 'react';
import { useLocation, Link } from 'react-router-dom';
import "./auth-form.css";

const AuthForm = () => {
  const location = useLocation(); // pour obtenir l'URL actuelle
  const isSignUp = location.pathname === '/sign-up'; // Vérifie si l'URL est pour l'inscription
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    firstName: '',
    lastName: '',
    userName: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.id]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
  };

  return (
    <div className="auth-container bg-dark">
      <section className="sign-in-content">
        {!isSignUp && (
          <i className="fa fa-user-circle sign-in-icon"></i>
        )}
        <h1>{isSignUp ? 'Sign Up' : 'Sign In'}</h1>
        <form onSubmit={handleSubmit}>
          {/*isSignUp && (
            <>
              <div className="input-wrapper">
                <label htmlFor="firstName">First Name</label>
                <input type="text" id="firstName" value={formData.firstName} onChange={handleChange} />
              </div>
              <div className="input-wrapper">
                <label htmlFor="lastName">Last Name</label>
                <input type="text" id="lastName" value={formData.lastName} onChange={handleChange} />
              </div>
              <div className="input-wrapper">
                <label htmlFor="userName">Username</label>
                <input type="text" id="userName" value={formData.userName} onChange={handleChange} />
              </div>
            </>
          )*/}
          <div className="input-wrapper">
            <label htmlFor="email">{isSignUp ? 'Email' : 'Username or Email'}</label>
            <input type={isSignUp ? 'email' : 'text'} id="email" value={formData.email} onChange={handleChange} />
          </div>
          <div className="input-wrapper">
            <label htmlFor="password">Password</label>
            <input type="password" id="password" value={formData.password} onChange={handleChange} />
          </div>
          {/* isSignUp && (
            <div className="input-wrapper">
              <label htmlFor="confirmPassword">Confirm Password</label>
              <input type="password" id="confirmPassword" value={formData.confirmPassword} onChange={handleChange} />
            </div>
          ) */}
          <div className="input-remember">
              <input type="checkbox" id="remember-me" />
              <label htmlFor="remember-me">Remember me</label>
          </div>
          <button className="sign-in-button" type="submit"></button>
        </form>
        <div className="sign-up-link">
            <p>
              Don't have an account? <Link to="/sign-up">Sign Up</Link>
            </p>
        </div>
      </section>
    </div>
  );
};

export default AuthForm;
