import axios from "axios";
import { loginUserFailed, loginUserRequest, loginUserSuccess, logOut } from "./reducer";

export const loginUser = (body, dispatch) => {
  dispatch(loginUserRequest());
  return axios.post("http://localhost:3001/api/v1/user/login", body)
    .then((res) => {
      if (body.rememberMe) {
        localStorage.setItem("token", res.data.body.token);
      }
      getUser(res.data.body.token, dispatch);
    })
    .catch(() => {
      alert("Un problème a été rencontré lors de la connexion.");
      dispatch(loginUserFailed());
    });
};

export const registerUser = (body) => {
  return axios.post("http://localhost:3001/api/v1/user/signup", body)
    .then(() => {
        alert('Utilisateur ajouté')
    })
    .catch(() => {
      alert("Un problème a été rencontré");
    });
};
export const getUser = (token, dispatch) => {
  dispatch(loginUserRequest());
  return axios.post("http://localhost:3001/api/v1/user/profile", null, {
      headers: { Authorization: "Bearer " + token },
    })
    .then((res) => {
      const payload = { user: res.data.body, token };
      dispatch(loginUserSuccess(payload));
      return res.data.body;
    })
    .catch(() => {
      alert("Un problème a été rencontré lors de la connexion.");
      dispatch(loginUserFailed());
    });
};

export const logOutUser = (dispatch) => {
    localStorage.removeItem('token');
    dispatch(logOut());
}
