import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./header.css";

const Header = () => {
  // Simuler l'état de connexion et le nom de l'utilisateur pour le moment
  const [isLoggedIn, setIsLoggedIn] = useState(false); // À remplacer par une vraie logique d'authentification
  const [userName, setUserName] = useState(""); // À remplacer par la vraie récupération du nom

  // Fonction pour se connecter/déconnecter (à remplacer par la vraie logique)
  const toggleLogin = () => {
    if (isLoggedIn) {
      setIsLoggedIn(false);
      setUserName("");
    } else {
      setIsLoggedIn(true);
      setUserName("Tony"); // Ce nom devrait être récupéré dynamiquement
    }
  };

  return (
    <header>
      <nav className="main-nav">
        <Link className="main-nav-logo" to="/index">
          <img className="main-nav-logo-image" src="../../img/argentBankLogo.png" alt="Argent Bank Logo"/>
          <h1 className="sr-only">Argent Bank</h1>
        </Link>
        <div>
          {isLoggedIn ? (
            <>
              <Link className="main-nav-item" to="/user">
                <i className="fa fa-user-circle"></i>
                {userName}
              </Link>
              <Link className="main-nav-item" to="/index" onClick={toggleLogin}>
                <i className="fa fa-sign-out"></i>
                Sign Out
              </Link>
            </>
          ) : (
            <Link className="main-nav-item" to="/sign-in" onClick={toggleLogin}>
              <i className="fa fa-user-circle"></i>
              Sign In
            </Link>
          )}
        </div>
      </nav>
    </header>
  );
};

export default Header;
